variables:
//Funcion that call the classic theme
var backToClassic = function() {
	$('.classic .switch div').animate({right: '-20px'},300);
	$('.classic .switch').css('background-color','rgba(0, 0, 0, 0.2)');
	$('#circle').fadeOut('fast');
	$('#i1').attr("src","media/1.png");
	$('#i2').attr("src","media/2.png");
	$('#i3').attr("src","media/3.png");
	$('#i4').attr("src","media/4.png");
	$('#i5').attr("src","media/5.png");
	$('#animation').css("background","#2f0916");
	$('#pallet-color span img').css('display','none');
	$('.custom').css("background","rgba(255, 255, 255, 0.5)");
};

//Colors of each span pallet
var c1Color = "#333";
	c2Color = "#535353";
	c3Color = "#16a085";
	c4Color = "#338000";
	c5Color = "#1BA1E2";
	c6Color = "#3B5998";
	c7Color = "#34495e";
	c8Color = "#441650";
	c9Color = "#f39c12";
	c10Color = "#c0392b";

//Spans of the pallet of color
//it returns to the colored theme
var palletColors = function() {
	$('#circle').fadeIn('fast');
	$('#i1').attr("src","media/1.svg");
	$('#i2').attr("src","media/2.svg");
	$('#i3').attr("src","media/3.svg");
	$('#i4').attr("src","media/4.svg");
	$('#i5').attr("src","media/5.svg");
	$('.classic .switch div').animate({right: 0},200);
	$('.classic .switch').css('background-color','rgba(255, 255, 255, 0.3)');
	$('.custom').css("background","rgba(255, 255, 255, 0.5)");
};

//Variable that change the clocks
var clock1 = function() {
	$('.t24 .switch div').animate({right: '-20px'},300);
	$('.t24 .switch').css('background-color','rgba(0, 0, 0, 0.2)');
	$('#clock').fadeOut('fast');
	$('#ur').fadeIn('fast');
	$('.t24').addClass('off');
	$('.t24').removeClass('on');
};
var clock2 = function(){
	$('.t24 .switch div').animate({right: '0'},300);
	$('.t24 .switch').css('background-color','rgba(255, 255, 255, 0.2)');
	$('#clock').fadeIn('fast');
	$('#ur').fadeOut('fast');
	$('.t24').addClass('on');
	$('.t24').removeClass('off');	
};
